INSERT INTO public."Generator_task" (id, task_template, answer, added_at, created_by, task_id, files, author) VALUES (50, '<p>На предприятии каждой изготовленной детали присваивают серийный номер из 105 символов. Используется посимвольное кодирование, все символы кодируются одинаковым минимально возможным числом бит. Для хранения всего серийного номера выделяется целое минимально возможное количество байт. Для хранения 65 536 номеров потребовалось не менее 7 Мбайт памяти. Определите минимально возможную мощность алфавита.</p>', '257', '2026-02-21 15:47:12+00', 'ФИПИ', 11, '', '');
