INSERT INTO public."Generator_task" (id, task_template, answer, added_at, created_by, task_id, files, author) VALUES (57, '<p>На предприятии каждой изготовленной детали присваивается серийный номер, содержащий десятичные цифры и символы из 2030-символьного специального алфавита. Используется посимвольное кодирование, где каждый символ кодируется минимально возможным целым количеством бит. Для хранения всего серийного номера выделяется целое минимально возможное количество байт. Для хранения 318 серийных номеров отведено более 67 Кбайт памяти. Определите минимально возможную длину серийного номера.</p>', '157', '2026-02-21 15:47:50+00', 'ФИПИ', 11, '', '');
