INSERT INTO public."Generator_task" (id, task_template, answer, added_at, created_by, task_id, files, author) VALUES (98, '<p><span style="background-color:rgb(255,255,255);color:rgb(0,0,0);">На рисунке справа схема дорог Н-ского района изображена в виде графа, звёздочка в ячейке таблицы обозначает наличие дороги между двумя пунктами. Так как таблицу и схему рисовали независимо друг от друга, то нумерация населённых пунктов в таблице никак не связана с буквенными обозначениями на графе.</span><br>&nbsp;</p><figure class="image image-style-side"><img style="aspect-ratio:345/176;" src="https://kpolyakov.spb.ru/cms/images/8150.gif" width="345" height="176"></figure><p><span style="background-color:rgb(255,255,255);color:rgb(0,0,0);">Определите, какие номера в таблице могут соответствовать населённым пунктам A и D на схеме. В ответе запишите эти два номера в возрастающем порядке без пробелов и знаков препинания.</span></p>', '25', '2026-02-21 15:59:18+00', 'ADMIN', 1, '', 'Апробация 2025');
