INSERT INTO public."Generator_task" (id, task_template, answer, added_at, created_by, task_id, files, author) VALUES (958, 'При проведении опыта вещество равномерно охлаждали в течение $8$ минут. При этом каждую минуту температура вещества уменьшалась на $5° C$. Найдите температуру вещества (в градусах Цельсия) через $3$ минуты после начала проведения опыта, если его начальная температура составляла $−10° C$.', '-25', '2026-02-28 16:05:55.008681+00', 'Admin_M', 76, '', 'ФИПИ');
