INSERT INTO public."Generator_task" (id, task_template, answer, added_at, created_by, task_id, files, author) VALUES (292, '<p>На числовой прямой дан отрезок $A = [3;60]$; $B$ — множество всех натуральных делителей числа $177$, отличных от единицы и от самого числа $177$; $C$ — множество всех натуральных делителей некоторого натурального числа $y$, отличных от единицы и от самого числа $y$ (число $y$ таково, что множество $C$ непустое). Укажите наибольшее возможное значение $y$, для которого выражение \[ (x \in C) \to \bigl((x \in A) \land (x \in B)\bigr) \] истинно (т.е. принимает значение $1$) при любом значении переменной $x$.</p>', '2809', '2026-02-27 16:13:17+00', 'ADMIN', 15, '', 'Сборник Крылова (вариант 1)');
