INSERT INTO public."Generator_task" (id, task_template, answer, added_at, created_by, task_id, files, author) VALUES (487, 'Пристани A и B расположены на озере, расстояние между ними равно 270 км. Баржа отправилась с постоянной скоростью из A в B. На следующий день после прибытия она отправилась тем же путём обратно со скоростью на 6 км/ч больше прежней, сделав по пути остановку на 12 часов. В результате она затратила на обратный путь столько же времени, сколько на путь из A в B. Найдите скорость баржи на пути из A в B. Ответ дайте в км/ч.', ' 9', '2026-02-27 20:48:20.95165+00', 'Admin_M', 53, NULL, 'ФИПИ');
